package candyhouse.sesameos.ir.baseimport com.google.gson.TypeAdapterimport com.google.gson.stream.JsonReaderimport com.google.gson.stream.JsonWriterclass HexTypeAdapter : TypeAdapter<Int>() {    override fun write(out: JsonWriter, value: Int?) {        if (value == null) {            out.nullValue()        } else {            out.value(value)        }    }    override fun read(input: JsonReader): Int {        val stringValue = input.nextString()        return if (stringValue.startsWith("0x")) {            stringValue.substring(2).toInt(16)        } else {            stringValue.toInt()        }    }}